import bechetFuncsimport pandas as pdimport bechetVarsprint("---MODEL RUN INFO---")if bechetVars.dataType == 'C':    print(f"    Climatology data run for {bechetVars.numberDays} days")    print("Model running...")elif bechetVars.dataType == 'H':    print(f"    Hourly data run for {bechetVars.numberDays} days")    print("Model running...")if bechetVars.dataType == 'H':    data = pd.read_csv(f'{bechetVars.filesPath}{bechetVars.inputFileName}') #df of input data    if bechetVars.obsFileName != 'NA':        observed = pd.read_csv(f'{bechetVars.filesPath}{bechetVars.obsFileName}') #observed data        observed['date'] = pd.to_datetime(observed['date'])    elif bechetVars.obsFileName == 'NA':        observed = 'NA'    bechetFuncs.main_simulation_loop(data, observed, bechetVars.T_wk,bechetVars.filesPath, bechetVars.numberDays, bechetVars.saveFile, bechetVars.outputPath) #run main sim loopelse: #if dataType == 'C':    data = pd.read_csv(f'{bechetVars.filesPath}{bechetVars.inputFileName}')    bechetFuncs.climatology_simulation_loop(data, bechetVars.T_wk,bechetVars.filesPath, bechetVars.numberDays, bechetVars.saveFile, bechetVars.outputPath)    print("Model run completed.")