import bechet_modelimport pandas as pdimport bechetVarsif bechetVars.dataType == 'H':    data = pd.read_csv(f'{bechetVars.filesPath}{bechetVars.inputFileName}') #df of input data    if bechetVars.obsFileName != 'NA':        observed = pd.read_csv(f'{bechetVars.filesPath}{bechetVars.obsFileName}') #observed data        observed['date'] = pd.to_datetime(observed['date'])    elif bechetVars.obsFileName == 'NA':        observed = 'NA'    bechet_model.main_simulation_loop(data, observed, bechetVars.T_wk,bechetVars.filesPath, bechetVars.numberDays, bechetVars.saveFile, bechetVars.outputPath) #run main sim loopelse: #if dataType == 'C':    data = pd.read_csv(f'{bechetVars.filesPath}{bechetVars.inputFileName}')    bechet_model.climatology_simulation_loop(data, bechetVars.filesPath, bechetVars.numberDays, bechetVars.saveFile, bechetVars.outputPath)    print("---MODEL RUN INFO---")if bechetVars.dataType == 'C':    print(f"Climatology data run for {bechetVars.numberDays} days")elif bechetVars.dataType == 'H':    print(f"Hourly data run for {bechetVars.numberDays} days")